// Video 160
// using type def instead of struct tag_name

#include <stdint.h>
#include <stdio.h>


// creating the structure we have been using as an excample in our study
// currently non-packed(aligned storage) as packed attibute is not used
typedef struct
{
	char data1;
	int data2;
	char data3;
	short data4;
}CarModel_t;  // creating an alias for the user defined data type struct

// creating a global struct variable
CarModel_t data; // as we saew this will consume 12 Bytes in Aligned Data storage form

int main(void)
{
	// initializing the values in out global struct variable
	data.data1 = 0xAA;
	data.data2 = 0xFFFFEEEE;
	data.data3 = 0x55;
	data.data4 = 0xA5A5;


	printf("data.data1 = %d\n",data.data1);
	printf("data.data2 = %d\n",data.data2);
	printf("data.data3 = %d\n",data.data3);
	printf("data.data4 = %d\n",data.data4);


    /* Loop forever */
	for(;;);
}
