// Video 188
// the difference in memory storage of String and String Literal(Ref ECB 59(ii))

#include <stdint.h>
#include <stdio.h>

int main(void)
{
    // create a sting using char array
	char msg1[] = "Hello How are you?"; // local array/variable.
	// come and goes during the runtime of the program
	// copied from FLASH memory into the array 'msg1' suring runtime of the program
	// Refer to ECB 60 (i)

	char *pmsg2 = "Fastbit lab";
	// better programming practice is adding a const: char const*pmg2 = "hello";

	// let us try to change the content
	msg1[0] = 'B'; // will change as in the Stack region of RAM
	pmsg2[0] = 'B'; // will have no effect as in the ROM only

	printf("The Message is: %s\n",msg1);// printing the string using %s
	printf("The Message is: %s\n",pmsg2);
	printf("The Address of 'msg1' is:    %p\n",&msg1);
	printf("Value of 'msg1' variable is: %p\n",msg1);
	printf("The Address of 'pmsg2' is:    %p\n",&pmsg2);
	printf("Value of 'pmsg2' variable is: %p\n",pmsg2);
	for(;;);
}
